class JsObject{getPropertyList(path){let res=path.replace("[",".").replace("]","").split(".");return res[0]===""&&res.shift(),res}getInstanceProperty(instance,propertyPath){if(propertyPath==="")return instance;let currentProperty=instance,splitProperty=this.getPropertyList(propertyPath);for(let i=0;i<splitProperty.length;i++)if(splitProperty[i]in currentProperty)currentProperty=currentProperty[splitProperty[i]];else return null;return currentProperty}}window.jsObject=new JsObject;